$(document).ready(function(){
    $('.image-section').hide();
    $('#predictbtn').hide();
        $("[data-bss-hover-animate]").mouseenter((function(){
                var a=$(this);
                a.addClass("animated "+a.attr("data-bss-hover-animate"))
            })).mouseleave((function(){
                var a=$(this);
                a.removeClass("animated "+a.attr("data-bss-hover-animate"))
        }))
});